package book.java;import java.util.Arrays;import java.util.Collections;import java.util.Comparator;import java.util.LinkedList;import java.util.List;public class JavaLibrary {	public static final List<Book> books = Arrays.asList(new Book("A", "A1"), new Book("A", "A2"), new Book("B", "B1"));	public static List<Book> booksByAuthor(String author) {		List<Book> result = new LinkedList<>();		for (Book b : books) {			if (b.getAuthor().equals(author))				result.add(b);		}		return result;	}	public static void main(String[] args) {		List<Book> booksByA = booksByAuthor("A");		Collections.sort(booksByA, new Comparator<Book>() {			public int compare(Book b1, Book b2) {				return b1.getTitle().compareTo(b2.getTitle());			}		});		System.out.print(booksByA);	}}